#include "libft.h"

char	*ft_strdup(const char *s1)
{
	size_t	i;
	char	*buffer;

	i = 0;
	buffer = (char *)malloc(ft_strlen(s1) + 1);
	if (buffer == NULL)
		return (NULL);
	while (s1[i] != '\0')
	{
		buffer[i] = s1[i];
		i++;
	}
	buffer[i] = '\0';
	return (buffer);
}